(24, 61, 0, 0, 0, NULL, NULL, NULL, NULL, 1, 'В каждом случае — индивидуально.', '2019-05-01 14:26:17', NULL),
(24, 62, 0, 0, 0, NULL, NULL, NULL, NULL, NULL, '', '2019-05-01 14:26:17', NULL),
